 * LICENSE file in the root directory of this source tree.
 */

function useAnnouncer(textCount, maxCount) {
  var lastTen = maxCount - 10;

  if (textCount >= lastTen) {
    return "".concat(maxCount - textCount, " characters left.");
  }
}

export { useAnnouncer };
